mapper.select:
select count(*) from board where 

	${searchOption}  LIKE '%'||#{keyword}||'%' and 

kind = #{kind}

SELECT * FROM (SELECT ROWNUM as RN, A.* FROM (
	SELECT * FROM BOARD where 

			${searchOption} LIKE '%'||#{keyword}||'%' and 

		kind = #{kind}
		ORDER BY boardnumber DESC 
	) A
)
WHERE RN BETWEEN #{start} AND #{end}
